function RenameMovePhotosdirpath=uigetdir(cd,'Move Files From');dirpath2=uigetdir(cd,'Move Files To');name=inputdlg('Enter name for files');name=name{1};d=getdir(dirpath);if strcmp(d(1).name,'Thumbs.db');    d(1)=[];endfor a=1:length(d);    if ~isdir([dirpath,'\',d(a).name])        suff=d(a).name(end-3:end);    %     fullname=[name,' ',num2str(a),suff];        fullname=[name,' ',addzerostonumberstring(a+0,3),suff];        movefile([dirpath,'\',d(a).name],[dirpath2,'\',fullname]);        h=waitbar(a/length(d));    endendclose (h)% for a=10:length(d);% % 	if ~strcmp(d(a).name(end-3),'.');% 		movefile([d(a).name],['0',num2str(9+a),d(a).name(end-3:end)]);% % 	end% end