function RenamePhotosdirpath=uigetdir;name=inputdlg('Enter name for files');name=name{1};d=getdir(dirpath);if strcmp(d(1).name,'Thumbs.db');    d(1)=[];endfor a=1:length(d);    if ~isdir([dirpath,'\',d(a).name])        suff=d(a).name(end-3:end);    %     fullname=[name,' ',num2str(a),suff];        fullname=[name,' ',addzerostonumberstring(a,3),suff];        movefile([dirpath,'\',d(a).name],[dirpath,'\',fullname]);        h=waitbar(a/length(d));    endendclose (h)