function remove_copydirpath=uigetdir('E:\DCIM\Temp2');d=getdir(dirpath);for a=1:length(d);    place=[];    name=d(a).name;    place=strfind(name,' copy');    if ~isempty(name)        name(place:(place+length(' copy')-1))=[];	    movefile([dirpath,'\',d(a).name],[dirpath,'\',name]);    end    h=waitbar(a/length(d));endclose (h)% for a=10:length(d);% % 	if ~strcmp(d(a).name(end-3),'.');% 		movefile([d(a).name],['0',num2str(9+a),d(a).name(end-3:end)]);% % 	end% end